// Utility functions
function getGradeBandFromGrade(grade) {
  if (grade <= 2) return "k-2";
  if (grade <= 5) return "3-5";
  if (grade <= 8) return "6-8";
  return "9-12";
}

function isValidGradeBand(value) {
  return ["k-2", "3-5", "6-8", "9-12"].includes(value);
}

